import recastai from 'recastai'

function textRequest(text, param) {
  return new Promise((resolve, reject) => {
    if (!text) {
      reject('no message to send')
    } else {
      const client = new recastai.Client(param.userToken, param.language)
      client.textRequest(text, (res, err) => {
        if (err) {
          reject(err)
        } else {
          resolve(res)
        }
      })
    }
  })
}

module.exports = textRequest
